package shop.service

import arrow.core.Either
import io.kotest.assertions.arrow.either.shouldBeLeft
import io.kotest.assertions.arrow.either.shouldBeRight
import io.mockk.MockKAnnotations
import io.mockk.every
import io.mockk.impl.annotations.MockK
import io.mockk.mockk
import org.junit.jupiter.api.Assertions
import org.junit.jupiter.api.BeforeEach
import org.junit.jupiter.api.Test
import shop.model.CustomerID
import shop.model.Picture
import shop.model.PictureID
import shop.repository.CustomerRepository
import shop.repository.PictureRepository
import shop.utils.IdGenerator
import java.util.*

class PictureServiceTest {
    @MockK
    lateinit var pictureRepository: PictureRepository

    @MockK
    lateinit var customerRepository: CustomerRepository

    @MockK
    lateinit var idGenerator: IdGenerator

    lateinit var service: PictureService

    @BeforeEach
    fun setUp() {
        MockKAnnotations.init(this)
        service = PictureService(pictureRepository, customerRepository, idGenerator)
    }

    @Test
    fun `it should save a valid image base64 encoded string`() {
        val expectedPicture = Picture(PictureID("anyId"), testMimeImageBase64)

        every { pictureRepository.save(expectedPicture) } returns expectedPicture
        every { idGenerator.generate() } returns "anyId"

        val uploadPictureAttempt = service.uploadPicture(testMimeImageBase64)

        Assertions.assertEquals(expectedPicture, (uploadPictureAttempt as Either.Right).b)
    }

    @Test
    fun `it should return an error when trying to save an invalid base64 string`() {
        val uploadPictureAttempt = service.uploadPicture("invalidBase64String")

        uploadPictureAttempt.shouldBeLeft()
    }

    @Test
    fun `it should retrieve customer's picture when customer and picture exist`() {
        val expectedPicture = Picture(PictureID("any"), "")

        every { customerRepository.findById(any()) } returns Optional.of(
                mockk {
                    every { picture } returns expectedPicture
                }
        )

        val retrievePictureAttempt = service.retrieveCustomerPicture(CustomerID("anyId"))

        retrievePictureAttempt.shouldBeRight(expectedPicture)
    }

    @Test
    fun `it should return an error when retrievein the picture for a customer that does not exist`() {
        every { customerRepository.findById(any()) } returns Optional.empty()

        val retrievePictureAttempt = service.retrieveCustomerPicture(CustomerID("anyId"))

        retrievePictureAttempt.shouldBeLeft()

    }

    val testMimeImageBase64 = "data:image/jpeg;base64,"
}